$(function(){$(".follow-btn").delegate(".unfollow","hover",function(e){if(e.type==="mouseenter")$(this).text("Unfollow").addClass("action");else $(this).text("Following").removeClass("action")});$(".follow-btn").delegate(".unfollow","click",function(e){e.preventDefault();var t=$(this);$.post($(this).attr("href"),function(){$(t).attr("class","follow").text("Follow").attr("href",$(t).attr("href").replace(/unfollow/i,"follow_all"));var e=+$("#followers").data("followers");$("#followers").data("followers",e-1);$("#followers").text(e-1)})});$(".follow-btn").delegate(".follow","click",function(e){e.preventDefault();var t=$(this);$.post($(this).attr("href"),function(){$(t).attr("class","unfollow").text("Following").attr("href",$(t).attr("href").replace(/follow_all/i,"unfollow"));var e=+$("#followers").data("followers");$("#followers").data("followers",e+1);$("#followers").text(e+1)})})})